import { Component,OnInit } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { EmployeeService } from '../employee.service';

@Component({
  selector: 'app-edit-employee',
  templateUrl: './edit-employee.component.html',
  styleUrls: ['./edit-employee.component.css']
})
export class EditEmployeeComponent implements OnInit{

  id:any;
  addEmployee:any='';
  constructor(private fb:FormBuilder,private routes:Router,private employeeservice:EmployeeService,private url:ActivatedRoute){
    this.addEmployee = fb.group({
       fullname:["",Validators.required],
       age:["",Validators.required],
       email:["",Validators.required],
       password:["",Validators.required],
    })
  }
ngOnInit(): void {

  this.id = this.url.snapshot.params["id"];
   console.log(this.id);
   this.employeeservice.singleEmployee(this.id).subscribe((data)=>{
    this.addEmployee.patch
   })

}
onSubmit(){
  console.log(this.addEmployee.value);
  this.employeeservice.addEmployee(this.addEmployee.value).subscribe((data:any)=>{
   console.log(data);
   this.routes.navigate(['/list-employee']);
  })
}

}
<div class="row">
    <div class="col md-12 text-center">
        <h2>Add Employee</h2>
    </div>


    <div class="row"> 
        <div class="card">
        <form [formGroup]="addEmployee" novalidate class="form">
          <div class="row">
            <div class="col md-2"></div>
            <div class="col md-4">Full Name</div>
            <div class="col md-4">
                <input type="text" placeholder="Enter the fullname" formControlName="fullname" class="form-control">
            </div>
            <div class="col md-2"></div>
          </div>
          <div class="row">
            <div class="col md-2"></div>
            <div class="col md-4">Age</div>
            <div class="col md-4">
                <input type="number" placeholder="Enter the age" formControlName="age" class="form-control">
            </div>
            <div class="col md-2"></div>
          </div>
          <div class="row">
            <div class="col md-2"></div>
            <div class="col md-4">Email</div>
            <div class="col md-4">
                <input type="email" placeholder="abcdef@gmail.com" formControlName="email" class="form-control">
            </div>
            <div class="col md-2"></div>
          </div>
          <div class="row">
            <div class="col md-2"></div>
            <div class="col md-4">Password</div>
            <div class="col md-4">
                <input type="password" placeholder="password" formControlName="password" class="form-control">
            </div>
            <div class="col md-2"></div>
          </div>

          <div class="row">
            <div class="col md-2"></div>
            <div class="col md-8">
                <button  class="btn btn-success" type="submit" (click)="onSubmit()">Add Employee</button>
            </div>
            <div class="col md-2"></div>
          </div>
        </form>
    </div>
</div>
</div>
